import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TIMELINE from '../assets/TIMELINE.png'
import NOLOGO from '../assets/NO LOGO.png'


function Education() {

  const [educationData, setEducationData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {

    axios
      .get('http://localhost:8080/api/education') 
      .then((response) => {
        setEducationData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching education data:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className='px-20 xl:py-24 grid grid-cols-12 gap-4'>

      <div className='xl:col-start-1 grid grid-rows-4 justify-items-end  sm:hidden'>
        {educationData.map((entry, index) => (
          <div key={index}>{entry.timeline}</div>
        ))}
      </div>

      <div className='justify-items-center'>
        <img src={TIMELINE} alt="" />
      </div>
      <div className='col-span-4 grid grid-rows-4 justify-items-start '>

          {educationData.map((entry, index) => (
          <div key={index} className="content-start mb-6">
            <div className="text-2xl font-semibold mb-3">{entry.qualification}</div>
            <div>{entry.institution}</div>
            <div>{entry.place}</div>
          </div>
        ))}


      </div>
      <div className="col-start-8 col-end-12 bg-zinc-800 rounded-xl justify-items-center content-center">
        <img src={NOLOGO} alt="" />
      </div>

    </div> 
  )
}

export default Education
