import React from 'react';
import { useLocation } from 'react-router-dom';

function ProjectDetails() {
  const location = useLocation();
  const { selectedProject } = location.state || {};

  if (!selectedProject) {
    return <div>No project selected.</div>;
  }

  return (
    <div className="p-6">
      <div className="text-3xl font-bold mb-4">{selectedProject.heading}</div>
      <div className="mb-4 text-xl">{selectedProject.subHeading}</div>
      <div className="mb-4 text-lg text-justify">
        {selectedProject.about || "No description available."}
      </div>
      <div className="mb-4 text-lg">Tools: {selectedProject.tools}</div>
      {selectedProject.projectLink && (
        <div className="mb-4 text-lg">
          <a href={selectedProject.projectLink} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:text-blue-700">
            View Project
          </a>
        </div>
      )}
    </div>
  );
}

export default ProjectDetails;
