import React, { useState, useEffect, useMemo } from 'react';

function Profile() {
  const roles = useMemo(() => ['DEVELOPER', 'DESIGNER', 'PHOTOGRAPHER'], []); // Memoized array
  const [currentRole, setCurrentRole] = useState(roles[0]);
  const [animate, setAnimate] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setAnimate(false); // Disable animation before role change
      setTimeout(() => {
        setCurrentRole((prevRole) => {
          const currentIndex = roles.indexOf(prevRole);
          const nextIndex = (currentIndex + 1) % roles.length;
          return roles[nextIndex];
        });
        setAnimate(true); // Re-enable animation after role change
      }, 200);
    }, 2000);

    return () => clearInterval(interval);
  }, [roles]);

  return (
    <div>
      <h1
        className={`text-4xl font-bold transition-transform duration-500 ease-in ${
          animate ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-5'
        }`}
      >
        I am {currentRole}
      </h1>
    </div>
  );
}

export default Profile;
