import React, { useEffect, useState } from 'react';
import axios from 'axios';
import POINTS from '../assets/TIMELINE.png'


function Projects() {

  const [projectData, setProjectData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedProject, setSelectedProject] = useState(null);

  useEffect(() => {

    axios
      .get('http://localhost:8080/api/projects') 
      .then((response) => {
        setProjectData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching education data:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className='px-20 xl:py-24 grid grid-cols-12 gap-4'>

      <div className='justify-items-center'>
        <img src={POINTS} alt="" />
      </div>

      <div className='col-span-4 grid grid-rows-4 justify-items-start '>

            {projectData.map((entry, index) => (
              <div className="content-start hover:cursor-pointer">
              <div key={index} 
              className='text-2xl font-semibold mb-3'
              onClick={() => setSelectedProject(entry)} 
              >
                {entry.heading}</div>
              <div>{entry.subHeading}</div>
              </div>
            ))}

      </div>

      <div className="col-start-7 col-end-12 rounded-xl p-6  text-white">
        {selectedProject ? ( // Check if a project is selected
          <div>
            <div className="text-3xl font-bold mb-4">{selectedProject.heading}</div>
            <div className="mb-48 text-xl">{selectedProject.subHeading}</div>
            <div className="mb-4 text-lg text-justify">{selectedProject.about || 'No description available.'}</div>
            <div className="mb-4 text-l">Tools: {selectedProject.tools}</div>
          </div>
        ) : (
          <div className="text-center text-gray-400">
            Select a project to view details here.
          </div>
        )}
      </div>
    </div>


  )
}

export default Projects;
